    };
  }

  return _obj;
}

function initF(fromUrl) {
  obj = init("wps", "100%", "100%");

  if (!ie && obj) {
    var Interval_control = setInterval(function () {
      app = obj.Application;

      if (app && app.IsLoad()) {
        clearInterval(Interval_control);
        app.openDocumentRemote(fromUrl, false);
      }
    }, 500);
  } else {
    app = obj;
    app.openDocumentRemote(fromUrl, false);
  }
}

