import tableList from '@/components/grid/TableList.vue';
export default {
  name: "MonthlyReportList",
  components: {
    tableList: tableList
  },
  data: function data() {
    return {
      uuids: '',
      packageStatus: this.$packageStatus,
      checkedData: [],
      //月报分页
      pagination: {
        currentPage: 1,
        pageSize: 10,
        pageSizes: [5, 10, 20, 50],
        //默认每页显示多少条
        position: 'center',
        layout: 'sizes ,total,prev, pager, next, jumper'
      },
      //月报产品分页
      //分页
      pagination1: {
        currentPage: 1,
        pageSize: 5,
        pageSizes: [5, 10, 20],
        //默认每页显示多少条
        position: 'center',
        layout: 'sizes ,total,prev, pager, next, jumper'
      },
      stryForm: {
        uuid: '',
        taxRevenue: "",
        totalRevenue: "",
        givenYearMonth: "",
        givenYear: "",
        givenMonth: "",
        powerCost: ""
      },
      productForm: {
        uuid: '',
        productName: "",
        majorDirectionDm: "",
        claimDirectionDm: "",
        areaDirectionDm: "",
        conclusion: "",
        productSellingRevenue: ""
      },
      rules: {},
      // 查询月报列表  内外网
      getDataWaystry: {
        dataType: "url",
        type: 'post',
        data: this.$api.hyglApi.queryComMonthly,
        param: {
          netType: this.$packageStatus === 'zwww' ? 'in' : 'ex'
        }
      },
      // 通过url方式
      getprojectDataWay: {
        dataType: "url",
        type: 'post',
        data: this.$api.hyglApi.queryClaimProduct,
        param: {
          flag: 'month',
          uuids: this.uuids
        }
      },
      columns: [{
        label: ' 所属年月',
        prop: 'yearMonth',
        template: true
      }, {
        label: '企业名称',
        prop: 'comName',
        width: '140'
      }, {
        label: '利润总额',
        prop: 'totalRevenue',
        width: '140'
      }, {
        label: '税收',
        prop: 'taxRevenue',
        width: '140'
      }, {
        label: '电力消费',
        prop: 'powerCost',
        width: '140'
      }, {
        label: '填报时间',
        prop: 'createTime',
        showOverflowTooltip: false
      }],
      projectInfocolumns: [{
        label: ' 产品名称',
        prop: 'productName'
      }, {
        label: '操作',
        prop: 'opts',
        template: true //添加模板，一般处理操作列(注意：tempate、formatter不能同时使用)

      }],
      dialogFormVisible: false,
      dialogprojectFormVisible: false,
      formLabelWidth: '120px',
      zdList: [],
      jbList: [],
      lyList: [],
      isNotNew: 'N',
      //判断是否新建按钮标识
      isNotProductUpdate: 'N',
      //判断是否是产品编辑按钮酒标识
      state: '',
      //控制月报是否可修改
      tableid: '',
      //只能选择本月以前包含本月的月份
      pickerOptions: {
        disabledDate: function disabledDate(time) {
          return time.getTime() > Date.now() - 8.64e7;
        }
      }
    };
  },
  methods: {
    //年份改变事件
    timeVal: function timeVal(val) {
      var arr = val.split('-');
      this.stryForm.givenYear = arr[0];
      this.stryForm.givenMonth = arr[1];
      var that = this;
      this.$$request({
        url: this.$api.hyglApi.getComMonthlyYMStatus,
        data: {
          uuid: this.stryForm.uuid,
          givenYear: this.stryForm.givenYear,
          givenMonth: this.stryForm.givenMonth
        }
      }).then(function (res) {
        if (res.result === 1) {
          if (res.info) {
            if (res.info === 'N') {
              that.stryForm.givenYearMonth = '';
              that.$message({
                message: val + '月不可使用，请重新选择',
                type: 'error'
              });
            }
          }
        } else {
          that.$alert(res.msg, "系统异常，请联系管理员！", {});
          return false;
        }
      }).catch(function (error) {
        that.$alert(error, "系统异常，请联系管理员！", {});
        return false;
      });
    },
    //新建
    add: function add() {
      this.stryForm = {
        uuid: '',
        taxRevenue: "",
        totalRevenue: "",
        givenYearMonth: "",
        givenYear: "",
        givenMonth: "",
        powerCost: ""
      };
      this.uuids = '';
      this.state = -1;
      this.isNotNew = 'Y';
      var that = this;
      this.$$request({
        url: this.$api.hyglApi.getComSelfcommentByAnnualId,
        data: {}
      }).then(function (res) {
        if (res.result === 1) {
          if (res.info) {
            if (res.info.state === 1) {
              that.dialogFormVisible = true;
            } else {
              that.$message({
                message: '请先保存并提交自评表',
                type: 'error'
              });
              return;
            }
          } else {
            that.$message({
              message: '请先保存并提交自评表',
              type: 'error'
            });
            return;
          }
        } else {
          that.$alert(res.msg, "系统异常，请联系管理员！", {});
          return false;
        }
      }).catch(function (error) {
        that.$alert(error, "系统异常，请联系管理员！", {});
        return false;
      });
    },
    //修改
    update: function update() {
      if (this.checkedData.length > 1 || this.checkedData.length == 0) {
        this.$alert("请选择一条数据进行修改！", "提示", {
          type: 'warning'
        });
      } else {
        this.dialogFormVisible = true;
        this.stryForm.powerCost = this.checkedData[0].powerCost;
        this.stryForm.uuid = this.checkedData[0].uuid;
        this.stryForm.totalRevenue = this.checkedData[0].totalRevenue;
        this.stryForm.taxRevenue = this.checkedData[0].taxRevenue;
        this.stryForm.givenYearMonth = this.checkedData[0].yearMonth;
        this.uuids = this.checkedData[0].uuids;
        this.state = this.checkedData[0].state;
        this.isNotNew = 'N';
      }
    },
    //产品添加按钮
    projectadd: function projectadd() {
      this.getZdList();
      this.dialogprojectFormVisible = true;
      this.productForm = {
        uuid: '',
        productName: "",
        majorDirectionDm: "",
        claimDirectionDm: "",
        areaDirectionDm: "",
        conclusion: "",
        productSellingRevenue: ""
      };
      this.isNotProductUpdate = 'N';
    },
    //产品添加
    submitForm: function submitForm(formName) {
      var _this = this;

      this.$refs[formName].validate(function (valid) {
        var that = _this;

        if (valid) {
          _this.$$request({
            url: _this.$api.hyglApi.saveClaimProduct,
            data: Object.assign(_this.productForm, {
              "isMonthly": "1",
              "state": "1"
            })
          }).then(function (res) {
            if (res.result === 1) {
              that.$message({
                message: '保存成功',
                type: 'success'
              });

              if (that.uuids) {
                that.uuids += ',' + res.info.uuid;
              } else {
                that.uuids = res.info.uuid;
              }

              that.dialogprojectFormVisible = false;
              that.$refs[formName].resetFields();
            } else {
              that.$message({
                message: '保存失败',
                type: 'error'
              });
            }
          });
        } else {
          return false;
        }
      });
    },
    //产品重置
    resetForm: function resetForm(formName) {
      this.$refs[formName].resetFields();
      this.productForm.majorDirectionDm = '';
      this.productForm.claimDirectionDm = '';
      this.productForm.areaDirectionDm = '';
    },
    //产品查看
    See: function See(row) {
      //查看
      this.dialogprojectFormVisible = true;
      this.productForm.uuid = row.uuid;
      this.productForm.productName = row.productName;
      this.productForm.conclusion = row.conclusion;
      this.productForm.productSellingRevenue = row.productSellingRevenue;
      this.$set(this.productForm, 'majorDirectionDm', row.majorDirectionDm);
      this.$set(this.productForm, 'claimDirectionDm', row.claimDirectionDm);
      this.$set(this.productForm, 'areaDirectionDm', row.areaDirectionDm);
      this.isNotProductUpdate = 'Y';
      this.getZdList();
    },
    //查询重点方向
    getZdList: function getZdList() {
      var that = this;
      this.$$request({
        url: this.$api.hyglApi.queryTableFromRedisByTableName,
        data: {
          tableName: 'DM_MAJOR_DIRECTION'
        }
      }).then(function (res) {
        if (res.result === 1) {
          that.zdList = res.info;
        }
      }).catch(function (error) {
        that.$alert(error, "系统异常，请联系管理员！", {});
      });
    },
    //产品删除
    del: function del(row) {
      var that = this;
      this.$$request({
        url: this.$api.hyglApi.deleteClaimProductByUuid,
        data: {
          uuid: row.uuid
        }
      }).then(function (res) {
        if (res.result == 1) {
          that.$message({
            message: '删除成功',
            type: 'success'
          });
          var arr = that.uuids.split(",");
          arr.splice(arr.indexOf(row.uuid), 1);
          that.uuids = arr.join(',');
        } else {
          that.$message({
            message: '删除失败',
            type: 'error'
          });
        }
      }).then(function () {
        that.$refs.tableInfo.loadData();
      }).catch(function (error) {
        that.$alert(error, "系统异常，请联系管理员！", {});
      });
    },
    //保存月报
    submitFormyb: function submitFormyb(formName) {
      var _this2 = this;

      var that = this;
      this.$refs[formName].validate(function (valid) {
        if (valid) {
          _this2.$$request({
            method: 'post',
            url: _this2.$api.hyglApi.saveComMonthly,
            data: Object.assign(_this2.stryForm, {
              "uuids": _this2.uuids
            })
          }).then(function (res) {
            if (res.result == 1) {
              that.$message({
                message: '保存成功',
                type: 'success'
              });
              that.$set(that.stryForm, 'uuid', res.info.uuid);
              that.$refs.table.loadData();
            } else {
              that.$message({
                message: '保存失败',
                type: 'error'
              });
            }
          });
        } else {
          return false;
        }
      });
    },
    //提交月报
    resetFormyb: function resetFormyb(formName) {
      var _this3 = this;

      var that = this;
      this.$refs[formName].validate(function (valid) {
        if (valid) {
          _this3.$axios({
            method: 'post',
            url: _this3.$api.hyglApi.saveComMonthly,
            data: Object.assign(_this3.stryForm, {
              "uuids": _this3.uuids
            })
          }).then(function (res) {
            if (res.result == 1) {
              that.$set(that.stryForm, 'uuid', res.info.uuid);
            }
          }).then(function () {
            that.changeStatus();
          });
        } else {
          return false;
        }
      });
    },
    //展示
    show: function show(row) {
      this.dialogFormVisible = true;
      this.stryForm.powerCost = row.powerCost;
      this.stryForm.uuid = row.uuid;
      this.stryForm.totalRevenue = row.totalRevenue;
      this.stryForm.taxRevenue = row.taxRevenue;
      this.stryForm.givenYearMonth = row.yearMonth;
      this.uuids = row.uuids;
      this.state = row.state;
      this.isNotNew = 'N';
    },
    //提交改变状态
    changeStatus: function changeStatus() {
      var that = this;
      this.$$request({
        url: that.$api.hyglApi.saveSubmitInfo,
        data: {
          state: '0',
          approvalDm: '020003',
          tableid: that.stryForm.uuid,
          title: that.stryForm.givenYear + '-' + that.stryForm.givenMonth + '月报',
          isMonthly: '1'
        }
      }).then(function (res) {
        if (res.result === 1) {
          that.$message({
            message: '提交成功',
            type: 'success'
          });
          that.dialogFormVisible = false;
          that.$refs.table.loadData();
        } else {
          that.$message({
            message: '提交失败',
            type: 'error'
          });
        }
      }).catch(function (error) {
        that.$alert(error, "系统异常，请联系管理员！", {});
        return false;
      });
    }
  },
  watch: {
    uuids: function uuids(newVal, oldVal) {
      if (newVal != oldVal) {
        this.$set(this.getprojectDataWay, 'param', {
          uuids: this.uuids,
          flag: 'temp'
        });

        if (this.$refs.tableInfo) {
          this.$refs.tableInfo.loadData();
        }
      }
    },
    'productForm.majorDirectionDm': function productFormMajorDirectionDm(val, oldVal) {
      var that = this;

      if (val !== oldVal) {
        if (this.isNotNew === 'Y') {
          //新建进来
          if (this.isNotProductUpdate === 'N') {
            this.productForm.claimDirectionDm = "";
            this.productForm.areaDirectionDm = "";
          }
        } else {
          if (this.state < 0) {
            //未提交
            if (this.isNotProductUpdate === 'N') {
              this.productForm.claimDirectionDm = "";
              this.productForm.areaDirectionDm = "";
            }
          }
        }

        this.lyList = [];

        if (val !== '') {
          this.$$request({
            url: this.$api.hyglApi.querySubSelcetListByTableNameAndParentCode,
            data: {
              tableName: "DM_CLAIM_DIRECTION",
              parentCode: val
            }
          }).then(function (res) {
            if (res.result === 1) {
              that.jbList = res.info;
            }
          }).catch(function (error) {
            that.$alert(error, "系统异常，请联系管理员！", {});
          });
        } else {
          this.jbList = [];
        }
      }
    },
    'productForm.claimDirectionDm': function productFormClaimDirectionDm(val, old) {
      var that = this;

      if (val !== old) {
        if (this.isNotNew === 'Y') {
          //新建进来
          if (this.isNotProductUpdate === 'N') {
            this.productForm.areaDirectionDm = "";
          }
        } else {
          if (this.state < 0) {
            //未提交
            if (this.isNotProductUpdate === 'N') {
              this.productForm.areaDirectionDm = "";
            }
          }
        }

        if (val !== '') {
          this.$$request({
            url: this.$api.hyglApi.querySubSelcetListByTableNameAndParentCode,
            data: {
              tableName: "DM_AREA_DIRECTION",
              parentCode: val
            }
          }).then(function (res) {
            if (res.result === 1) {
              that.lyList = res.info;
            }
          }).catch(function (error) {
            that.$alert(error, "系统异常，请联系管理员！", {});
          });
        } else {
          this.lyList = [];
        }
      }
    }
  }
};
