/*调*/
.tzggStyle /deep/ .ql-bubble .ql-picker {
    height: 40px
}

#formPage {
    margin: 10px auto;
    width: 80%;
    padding: 0px 22px;
    box-sizing: border-box;
    background-color: #fff
}

.tzggStyle /deep/ .el-select__caret el-input__icon el-icon-arrow-up {
    width: 5px
}

.tzggStyle /deep/ .el-dialog {
    display: flex;
    flex-direction: column;
    margin: 0 !important;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    /*height:600px;*/
    max-height: calc(100% - 30px);
    max-width: calc(100% - 30px);
}

.tzggStyle /deep/ .el-dialog .el-dialog__body {
    flex: 1;
    overflow: auto;
}

.tzggStyle /deep/ .ql-container.ql-snow {
    min-height: 200px;
}

.el-dialog-div {
    overflow: auto;
}

.tzggStyle /deep/ .el-dialog__body::-webkit-scrollbar {
    width: 2px;
    height: 100%;
}

.myupload .el-dialog__body {
    padding: 10px 20px;
    font-size: 16px;
}

.myupload .el-dialog__body-myi {
    color: #EED46F;
    margin-right: 20px;
}

.mytip {
    font-size: 12px;
    color: #909399;
    margin: 3px;
}

.tzggStyle /deep/ .editor {
    line-height: normal !important;
    height: 500px;
}

.ql-snow .ql-tooltip[data-mode=link]::before {
    content: "请输入链接地址:";
}

.ql-snow .ql-tooltip.ql-editing a.ql-action::after {
    border-right: 0px;
    content: '保存';
    padding-right: 0px;
}

.ql-snow .ql-tooltip[data-mode=video]::before {
    content: "请输入视频地址:";
}

.ql-snow .ql-picker.ql-size .ql-picker-label::before,
.ql-snow .ql-picker.ql-size .ql-picker-item::before {
    content: '14px';
}

.ql-snow .ql-picker.ql-size .ql-picker-label[data-value=small]::before,
.ql-snow .ql-picker.ql-size .ql-picker-item[data-value=small]::before {
    content: '10px';
}

.ql-snow .ql-picker.ql-size .ql-picker-label[data-value=large]::before,
.ql-snow .ql-picker.ql-size .ql-picker-item[data-value=large]::before {
    content: '18px';
}

.ql-snow .ql-picker.ql-size .ql-picker-label[data-value=huge]::before,
.ql-snow .ql-picker.ql-size .ql-picker-item[data-value=huge]::before {
    content: '32px';
}

.ql-snow .ql-picker.ql-size .ql-picker-label::before, .ql-snow .ql-picker.ql-size .ql-picker-item::before {
    content: '文本';
}

.ql-snow .ql-picker.ql-header .ql-picker-label[data-value="1"]::before,
.ql-snow .ql-picker.ql-header .ql-picker-item[data-value="1"]::before {
    content: '标题1';
}

.ql-snow .ql-picker.ql-header .ql-picker-label[data-value="2"]::before,
.ql-snow .ql-picker.ql-header .ql-picker-item[data-value="2"]::before {
    content: '标题2';
}

.ql-snow .ql-picker.ql-header .ql-picker-label[data-value="3"]::before,
.ql-snow .ql-picker.ql-header .ql-picker-item[data-value="3"]::before {
    content: '标题3';
}

.ql-snow .ql-picker.ql-header .ql-picker-label[data-value="4"]::before,
.ql-snow .ql-picker.ql-header .ql-picker-item[data-value="4"]::before {
    content: '标题4';
