import TableList from "@/components/grid/TableList";
import toolbar from "@/components/toolbar/myToolbar";
import createbar from "@/views/xtgl/myToolbar/tzggCreOperation";
import _ from 'lodash'
export default {
    name: "cgfb2",
    components: {
        TableList,
        toolbar,
        createbar
    },
    data() {
        return {
            searchTip: '',
            packageStatus: this.$packageStatus,
            createOrgNameList: [],//查询页部门列表
            stateList: [
                {
                    caption: '未发布',
                    value: 0,
                },
                {
                    caption: '审核中',
                    value: 2,
                    formatter: (row) => {
                        return this.getState(row.value)
                    }
                },
                {
                    value: 9,
                    caption: '已发布',
                },
            ],
            xjDialogVisible: false,
            dialogData: {},
            checkedData: [], //全选框
            uuids: [],
            //工具栏
            toolbarItem:[
                {
                    type:"primary",
                    title:"新建",
                    icon:"el-icon-edit-outline",
                    disabled:false,
                    click:"create"
                },
                {
                    type:"primary",
                    title:"修改",
                    icon:"el-icon-edit",
                    disabled:false,
                    click:"update"
                },
                {
                    type:"primary",
                    title:"删除",
                    icon:"el-icon-delete",
                    disabled:false,
                    click:"delete"
                },
            ],
            //查询
            searchParams: {
                title: '',
                createUserName: '',
                createTimeStart: '',
                createTimeEnd: '',
                state: '',
                createOrg:'',
                timeStamp: [],
            },
            //分页
            pagination: {
                currentPage: 1,
                pageSize: 10,
                position: 'center',
                pageSizes: [10, 50, 100],//默认每页显示多少条
                layout: 'sizes ,prev, pager, next, jumper'
            },
            //表单字段
            columns: [
                {
                    label: '标题',
                    prop: 'title',
                    width: '180',
                    showOverflowTooltip: true,
                    template: true
                },
                {
                    label: '发布人',
                    prop: 'createUserName',
                },
                {
                    label: '发布部门',
                    prop: 'createOrgName',
                },
                {
                    label: '发布日期',
                    prop: 'createTime',
                    formatter(row) {
                        return new Date(row.createTime).Format("yyyy-MM-dd hh:mm:ss")
                    },
                },
                {
                    label: '发布状态',
                    prop: 'stateName',
                    formatter: (row) => {
                        return this.getCaption(row.state)
                    }
                },
                {
                    label:'是否置顶',
                    prop:'isTop',
                    template: true
                }
            ],
            //调用组件传输返回页面数据
            getDataWay: {
                dataType: "url",
                data: this.$api.xtglApi.xtglTzggQuery,
                type: 'post',
                param: {proclamationDm: '0010'}
            },
        }
    },
    methods: {
        //查询时间处理
        timeSet(timeStamp){
            if(timeStamp[0] !== '' && timeStamp[1] !== ''){
                this.searchParams.createTimeStart = timeStamp[0]
                this.searchParams.createTimeEnd = timeStamp[1]
            }
        },
        //状态初始化
        getCaption(data){
            if(data <= 1){
                return '未发布'
            }else if(data > 1 && data < 9){
                return '审核中'
            }else if(data == 9){
                return '已发布'
            }
        },
        //弹窗初始化
        dialogInit() {
            this.xjDialogVisible = true
        },
        //页面刷新
        refreshTable(refresh) {
            this.xjDialogVisible = false
            if (refresh == true) {
                this.checkedData = [];
                this.$refs.table.loadData()
            }
        },
        //点击工具栏跳转
        toolbarClick(item, event) {
            const that = this;
            //const fromType = '0001'
            if (item.title == '新建') {
                this.dialogData = {};
                that.dialogData.showTip = true;
                that.dialogData.allowUpload = false;
                that.dialogData.fromType = '0010';
                that.dialogData.spType = '010010';
                this.xjDialogVisible = true;
            }
            if (item.title == '修改') {
                /*修改初始化*/
                if (this.checkedData.length == 1) {
                    if (this.checkedData[0].state > 1 && this.checkedData[0].state < 9) {
                        this.$alert('审核状态中无法修改！', '提示', {
                            type: 'warning'
                        })
                    } else if(this.checkedData[0].state == 9){
                        this.$alert('已发布内容无法修改！', '提示', {
                            type: 'warning'
                        })
                    } else if(this.checkedData.editFlag===0){
                        this.$alert('当前登录人不具有修改权限！', '提示', {
                            type: 'warning'
                        })
                    } else {
                        that.dialogData = this.checkedData[0];
                        that.dialogData.showTip = false;
                        that.dialogData.allowUpload = true;
                        that.dialogData.fromType = '0010';
                        that.dialogData.spType = '010010';
                        this.xjDialogVisible = true;
                    }
                } else {
                    this.checkedData = []
                    this.$refs.table.loadData()
                    this.$alert('请选中一条数据进行修改！', '提示', {
                        type: 'error'
                    })
                }
            }
            if (item.title == '删除') {
                if (that.checkedData.length < 1) {
                    that.$alert('请选择至少一条数据进行删除！', '提示', {
                        type: 'warning'
                    })
                } else if (that.checkedData.length >= 1) {
                    let isDel = true
                    if (isDel) {
                        this.checkedData.forEach((item) => {
                            if (item.state > 1 && item.state < 9) {
                                this.$alert('审核状态中无法删除！', '提示', {
                                    type: 'warning'
                                })
                                isDel = false
                            } else if (item.state == 9) {
                                this.$alert('已发布内容无法删除！', '提示', {
                                    type: 'warning'
                                })
                                isDel = false
                            }else if(this.checkedData.editFlag===0){
                                this.$alert('当前登录人不具有修改权限！', '提示', {
                                    type: 'warning'
                                })
                                isDel = false
                            }
                        })
                    }
                    if (isDel) {
                        let ids = ''
                        this.checkedData.forEach((item) => {
                            ids += (item.uuid + ',')
                        })
                        that.$confirm('确认删除?', '提示', {
                            confirmButtonText: '确定',
                            cancelButtonText: '取消',
                            type: 'warning'
                        }).then(() => {
                            that.$axios({
                                method: 'post',
                                url: that.$api.xtglApi.xtglTzggDelete,
                                data: {
                                    uuid: ids
                                },
                            }).then(
                                function (res) {
                                    if (res.result == 1) {
                                        that.$message({
                                            message: res.msg,
                                            type: 'success'
                                        });
                                        that.checkedData = [];
                                        that.$refs.table.loadData()
                                    } else {
                                        that.$message({
                                            type: 'warning',
                                            message: '删除失败!'
                                        });
                                    }
                                })
                        }).catch(() => {
                        });

                    }

                }
            }
        },
        //点击标题跳转页面方法
        selectGg(data) {
            this.$router.push({
                name: "detail",
                query: {
                    uuid: data.uuid,
                    moduleCode: 'cgfb',
                }
            })
        },
        //查询---防抖函数，限制后台访问频率
        searchBtn: _.debounce(function () {
            const that = this;
            if (this.searchParams.title.indexOf('?') === -1) {
                this.searchTip = ''
            }
            let param = {
                title: that.searchParams.title,
            }
            that.$set(that.getDataWay.params = param);
            that.$refs.table.loadData(param);
        }, 500),
    },
    watch: {
        'searchParams.title': {
            handler(newParams, oldParams) {
                this.searchTip = '正在输入...'
            },
            deep: true,
            immediate: true
        },
    },
    created(){
        this.searchTip = '' //首次进入清除提示
    },
    mounted() {
        const that = this
