import TableList from "../../../components/grid/TableList";

export default {
    name: "refreshRedis",
    components: {
        TableList,
    },
    data() {
        return {
            checkedData: [], //全选框
            //表单字段
            columns: [
                {
                    label: '表名',
                    prop: 'tableName',

                },
                /*{
                    label: '索引列',
                    prop: 'indexColumns'
                },*/
                {
                    label: 'SQL脚本',
                    prop: 'dataQuerySql',
                },
                {
                    label: '分组列',
                    prop: 'groupIndex'
                },
                {
                    label: '存储类型',
                    prop: 'redisType',

                },
                /*{
                    label: '说明',
                    prop: 'indexCaption',
                },*/
            ],
            //调用组件传输返回页面数据
            getDataWay: {
                dataType: "url",
                data: this.$api.xtglApi.queryRedisTable,
                type: "post",
                param: {},
            },
        }
    },
    methods: {
        //直接全部刷新
        refreshTable() {
            const that = this
            this.$$request({
                method:'post',
                url:this.$api.xtglApi.refreshRedis,
                data:{
                    'tableName': ''
                },
            }).then(res => {
                if(res.result == 1){
                    that.$message({
                        message: '缓存刷新成功！',
                        type: 'success'
                    })
                }else{
                    that.$message({
                        message: '缓存刷新失败!',
                        type: 'error'
                    })
                }
                this.checkedData = [];
                this.$refs.table.loadData()
            }).catch(function (err) {
                that.$alert(err, "系统异常，请联系管理员！", {});
            })
        },
        //页面刷新
        refreshTable2() {
            const that = this
            let refreshParam = ''
            //暂时无批量刷新，但可一键刷新全部
            if(this.checkedData.length > 1) {
                this.$alert('请选中一条数据进行刷新！','提示',{
                    type: 'warning'
                })
                return false
            }else if(this.checkedData.length == 1) {
                //预留批量刷新方法
                this.checkedData.forEach((item) => {
                    refreshParam += (item.tableName + ',')
                })
            }
            this.$$request({
                method:'post',
                url:this.$api.xtglApi.refreshRedis,
                data:{
                    'tableName': refreshParam
                },
            }).then(res => {
                if(res.result == 1){
                    that.$message({
                        message: '缓存刷新成功！',
                        type: 'success'
                    })
                }else{
                    that.$message({
                        message: '缓存刷新失败!',
                        type: 'error'
                    })
                }
                this.checkedData = [];
                this.$refs.table.loadData()
            }).catch(function (err) {
                that.$alert(err, "系统异常，请联系管理员！", {});
            })
        },
    },
}
