export default {
    name: "approval",
    props: {
        apprData: {
            type: Object
        }
    },
    data() {
        return {
            dialogVisible: false,
            text:""
        }
    },
    methods: {
        pass() {//审批通过
            let that = this;
            console.log(this.apprData)
            this.$axios({
                method: 'post',
                // url: "/ApprovalCtrl/approvalPass",
                url: this.$api.xtglApi.approvalPass,
                data: {
                    uuid: this.apprData.approvalId,
                    tableid:this.apprData.uuid,  //增传tableid进审核通过接口
                    moduleCode: this.apprData.moduleCode, //增传modelCode进审核通过接口
                    taskId: this.apprData.taskId, //增传taskId进审核通过接口
                }
            }).then((res) => {
                if (res.result === 1) {
                    that.$message({
                        type: 'success',
                        message: res.msg
                    })
                    that.$router.push({
                        name: 'dbList'
                    });
                }
            }).catch(function (err) {
                that.$message({
                    type: 'success',
                    message: '审批失败'
                })
            })
        },
        fail() {//审批不通过
            this.dialogVisible=true;
        },
        sure(){
            let that = this;
            console.log(this.apprData)
            this.$axios({
                method: 'post',
                // url: "/ApprovalCtrl/approvalFail",
                url: this.$api.xtglApi.approvalFail,
                data: {
                    uuid: this.apprData.approvalId,
                    tableid:this.apprData.uuid,  //增传tableid进审核通过接口
                    moduleCode: this.apprData.moduleCode, //增传modelCode进审核通过接口
                    taskId: this.apprData.taskId, //增传taskId进审核通过接口
                    text:this.text
                }
            }).then((res) => {
                if (res.result === 1) {
                    that.$message({
                        type: 'success',
                        message: res.msg
                    });
                    this.dialogVisible=false;
                    that.$router.push({
                        name: 'dbList'
                    });
                }
            }).catch(function (err) {
                that.$message({
                    type: 'success',
                    message: '审批失败'
                })
            })
        },
        cancel(){
            this.dialogVisible=false;
        },
        handleClose(){
            this.text="";
        }
    }
}
